mapper.select:
SELECT
	COUNT(DISTINCT(id)) AS contractNum
FROM
	`tb_contract`
WHERE
	DATE_FORMAT(`create_time`,'%Y-%m-%d') BETWEEN #{startTime}
		AND #{endTime}
  AND create_by = #{username}
  AND `status` = 4

SELECT
	CAST(
			IFNULL(SUM(h.`contract_order`),0) AS DECIMAL (30, 0)
		) AS salesAmount
FROM
	`tb_contract` h
WHERE
	h.create_by = #{username}
    AND DATE_FORMAT(h.create_time,'%Y-%m-%d') BETWEEN #{startTime}
	AND #{endTime}

SELECT COUNT(DISTINCT (id)) AS tofollowedCluesNum
FROM `tb_clue`
WHERE id IN
	  (
		  SELECT assign_id
		  FROM `tb_assign_record`
		  WHERE user_name = #{username}
			AND DATE_FORMAT(create_time, '%Y-%m-%d') BETWEEN #{startTime}
			  AND #{endTime}
			AND `latest` = 1
			AND `type` = 0
	  )
  AND `status` = 1

SELECT COUNT(DISTINCT (id)) AS tofollowedBusinessNum
FROM `tb_business`
WHERE id IN
	  (
		  SELECT assign_id
		  FROM `tb_assign_record`
		  WHERE user_name = #{username}
			AND DATE_FORMAT(create_time, '%Y-%m-%d') BETWEEN #{startTime}
			  AND #{endTime}
			AND `latest` = 1
			AND `type` = 1
	  )
  AND `status` = 1
